import React, { useState, useEffect } from 'react'
import Node from './Node';
import './Pathfind.css';
import { BFS, drawPath } from './Bfs';

//GLOBAL
const cols = 38;
const rows = 20;


const Pathfind = () => {
    const [Grid, setGrid] = useState([]);
    const [togglePath, setTogglePath] = useState(0);
    const [startCords, setStartCords] = useState([0, 0])
    const [endCords, setEndCords] = useState([19, 37])


    const START_ROW = startCords[0];
    const START_COL = startCords[1];
    const END_ROW = endCords[0];
    const END_COL = endCords[1];

    useEffect(() => {
        initalizeGrid();
    }, [togglePath]);

    //Inicializace gridu
    const initalizeGrid = () => {
        const grid = [];

        //Filling grid with spots
        for (let i = 0; i < rows; i++) {
            grid[i] = [];
            for (let j = 0; j < cols; j++) {
                grid[i][j] = new Spot(i, j);
            }
        }

        let start = grid[START_ROW][START_COL];
        let end = grid[END_ROW][END_COL];

        // console.log(start)
        // console.log(end)

        setGrid(grid);

        const dict = BFS(grid, start, end);
        const path = drawPath(dict, [start.y, start.x], [end.y, end.x]);

        if (togglePath === 1) {
            fillPath(grid, path);
        }
    };

    // Vytvoreni jednotlivych spotu v gridu
    function Spot(y, x) {
        this.y = y;
        this.x = x;
        this.isStart = this.y === START_ROW && this.x === START_COL;
        // console.log(this.y)
        this.isEnd = this.y === END_ROW && this.x === END_COL;
        this.searched = false;
        this.isPath = false;
    };

    //Nasteveni promene isPath v kazdem spotu ktery je soucasti cesty na true
    const fillPath = (grid, path) => {
        for (let i = 0; i < path.length; i++) {
            grid[path[i][0]][path[i][1]].isPath = true;
        }
    }

    const onDrop = (e) => {
        e.preventDefault();
        let data = e.dataTransfer.getData("text");
        e.target.appendChild(document.getElementById(data));
        if (data === '0-0') {
            let cords = e.nativeEvent.srcElement.id;
            let startcords = cords.split("-");
            setStartCords(startcords);
        } else {
            let cords = e.nativeEvent.srcElement.id;
            let endcords = cords.split("-");
            setEndCords(endcords);
        }
        console.log(e.nativeEvent)
    }

    // console.log(startCords[0])
    // console.log(startCords[1])


    // console.log(endCords[0])
    // console.log(endCords[1])

    const onDragOver = (e) => {
        e.preventDefault();
    }

    // Funkce na vykresleni gridu
    const gridwithNode = (
        <div className="Grid" onDrop={(e) => (onDrop(e))} onDragOver={(e) => (onDragOver(e))}>
            {Grid.map((row, rowIndex) => {
                return (
                    <div key={rowIndex} className='rowWrapper'>
                        {row.map((col, colIndex) => {
                            const { isStart, isEnd, searched, isPath } = col;
                            return (
                                <Node key={colIndex}
                                    isPath={isPath}
                                    isStart={isStart}
                                    isEnd={isEnd}
                                    searched={searched}
                                    row={rowIndex}
                                    col={colIndex}
                                />
                            )
                        })}
                    </div>
                )
            })}
        </div>
    )

    return (
        <div className='Wrapper'>
            <h1>Pathfinding</h1>
            {/* Vykresleni gridu */}
            {gridwithNode}
            <button onClick={() => setTogglePath(1)}>Show path</button>
            <button onClick={() => setTogglePath(0)}>Hide path</button>
        </div >
    )
}

export default Pathfind
