import React, { useState, useEffect } from 'react'
// import uniqueId from 'react-html-id'
import Node from './Node';
import './Pathfind.css';
import { BFS } from './Bfs';

const cols = 45;
const rows = 20;
const START_ROW = 0;
const START_COL = 0;
const END_ROW = rows - 1;
const END_COL = cols - 1;


const Pathfind = () => {
    const [Grid, setGrid] = useState([]);

    useEffect(() => {
        initalizeGrid();
    }, []);

    //Vytvoreni gridu
    const initalizeGrid = () => {
        const grid = [];

        for (let i = 0; i < rows; i++) {
            grid[i] = [];
            for (let j = 0; j < cols; j++) {
                grid[i][j] = new Spot(i, j);
            }
        }

        const start = grid[START_ROW][START_COL];
        const end = grid[END_ROW][END_COL];

        setGrid(grid);

        BFS(grid, start, end);
    };

    // Vytvoreni bodu
    function Spot(x, y) {
        this.x = x;
        this.y = y;
        this.isStart = this.x === START_ROW && this.y === START_COL;
        this.isEnd = this.x === END_ROW && this.y === END_COL;
        this.searched = false;
    };

    // Vykresleni gridu
    const gridwithNode = (
        <div>
            {Grid.map((row, rowIndex) => {
                return (
                    <div key={rowIndex} className='rowWrapper'>
                        {row.map((col, colIndex) => {
                            const { isStart, isEnd, searched } = col;
                            return (
                                <Node key={colIndex} isStart={isStart} isEnd={isEnd} row={rowIndex} col={colIndex} searched />
                            )
                        })}
                    </div>
                )
            })}
        </div>
    )

    return (
        <div className='Wrapper'>
            <h1>Pathfinding</h1>
            {gridwithNode}
        </div>
    )
}

export default Pathfind
