        //     enddiv.draggable = false;

        //     if (!document.getElementById('temp-9-9')) {
        //         document.getElementById('node-9-9').appendChild(enddiv);
        //     }
        // }
    }

    const onDragStart = (e) => {
        e.dataTransfer.setData("text", e.target.id);
        console.log(e)
    }

    const onDragOver = (e) => {
        e.dataTransfer.setData("text", e.target.id);
    }

    return (
        <div
            id={`node-${row}-${col}`}
            onDrop={(e) => (onDrop(e))}
            onDragOver={(e) => (onDragOver(e))}
        >
            <div
                id={`${row}-${col}`}
                className={`node ${classes}`}
                draggable={draggAble}
                onDragStart={(e) => {
                    if (draggAble) {
                        onDragStart(e)
                    } else {
                        e.preventDefault()
                    }
                }}
            />
        </div>
    )
}

export default Node;
