const visited = [];
let path = [];

function Spot(x, y) {
    this.x = x;
    this.y = y;
    this.searched = false;
    this.isPath = false;
}

const findNeighbors = (grid, cord) => {
    let neighbors = [];

    //Nahoru
    if (cord[0] + 1 < grid.length) {
        neighbors.push([cord[0] + 1, cord[1]])
    }

    //Dolu
    if (cord[0] - 1 >= 0) {
        neighbors.push([cord[0] - 1, cord[1]])
    }

    //Doleva
    if (cord[1] - 1 >= 0) {
        neighbors.push([cord[0], cord[1] - 1])
    }

    //Doprava
    if (cord[1] + 1 < grid[0].length) {
        neighbors.push([cord[0], cord[1] + 1])
    }

    fillNeighbors(neighbors);

    return neighbors;
}

const fillNeighbors = (neigh) => {
    neigh.forEach((el, idx, arr) => {
        // console.log(el[0])
        let x = el[0];
        let y = el[1];
        arr[idx] = new Node(x, y);
    });
};

export const BFS = (grid, start, end) => {
    let queue = []
    start.searched = true
    queue.push(start)


    while (queue.length > 0) {
        let current = queue.shift();
        visited.push(current);
        if (current === end) {
            console.log("Found");
            break;
        } else {

            let neighbors = findNeighbors(grid, [current.x, current.y])


            neighbors.forEach(el => {
                let mem = grid[el.x][el.y];
                if (grid[el.x][el.y].searched === false) {
                    mem.searched = true;
                    queue.push(mem);
                };
            });
        }

        path.push(queue.pop())
    };
    console.log(path)

    path.forEach((el) => {
        let mem = grid[el.x][el.y];
        if (grid[el.x][el.y].path === false) {
            mem.path = true;
        }
    })
};
