import React from 'react';
import './Node.css';

const Node = ({ isStart, isEnd, searched, isPath, row, col }) => {
    const classes = isStart ? "node_start" : isEnd ? "node_end" : isPath ? "node_path" : "";
    const draggAble = isStart ? true : isEnd ? true : false;

    const onDrop = (e) => {
        e.preventDefault();
        let data = e.dataTransfer.getData("text");

        if (data === '0-0') {
            const startdiv = document.createElement('div');
            startdiv.id = 'temp-0-0';
            startdiv.className = 'node';
            startdiv.draggable = false;

            if (!document.getElementById('temp-0-0')) {
                document.getElementById('node-0-0').appendChild(startdiv);
            }
        }

        if (data === '19-37') {
            const enddiv = document.createElement('div');
            enddiv.id = 'temp-19-37';
            enddiv.className = 'node';
            enddiv.draggable = false;

            if (!document.getElementById('temp-19-37')) {
                document.getElementById('node-19-37').appendChild(enddiv);
            }
        }
    }

    const onDragStart = (e) => {
        e.dataTransfer.setData("text", e.target.id);
    }

    const onDragOver = (e) => {
        e.dataTransfer.setData("text", e.target.id);
    }

    return (
        <div
            id={`node-${row}-${col}`}
            onDrop={(e) => (onDrop(e))}
            onDragOver={(e) => (onDragOver(e))}
            className={`node ${classes}`}
        >
            <div
                id={`${row}-${col}`}
                draggable={draggAble}
                onDragStart={(e) => {
                    if (draggAble) {
                        onDragStart(e)
                    } else {
                        e.preventDefault()
                    }
                }}
            />
        </div>
    )
}

export default Node;
