import React, { useState, useEffect } from 'react'
// import uniqueId from 'react-html-id'
import Node from './Node';
import './Pathfind.css';
import { BFS, showPath } from './Bfs';

//GLOBAL
const cols = 45;
const rows = 20;
const START_ROW = 0;
const START_COL = 0;
const END_ROW = rows - 1;
const END_COL = cols - 1;


const Pathfind = () => {
    const [Grid, setGrid] = useState([]);
    // const [targetDiv, setTargetDiv] = useState("");
    const [startGrid, setStartGrid] = useState(0);

    useEffect(() => {
        initalizeGrid();
    }, [startGrid]);

    //Inicializace gridu
    const initalizeGrid = () => {
        const grid = [];

        //Filling grid with spots
        for (let i = 0; i < rows; i++) {
            grid[i] = [];
            for (let j = 0; j < cols; j++) {
                grid[i][j] = new Spot(i, j);
            }
        }

        setGrid(grid);
    };

    const startFinding = () => {
        const start = Grid[START_ROW][START_COL];
        const end = Grid[END_ROW][END_COL];


        const dict = BFS(Grid, start, end);
        const path = showPath(dict, [start.y, start.x], [end.y, end.x]);

        fillPath(Grid, path);
        return (
            { gridwithNode }
        )
    }

    // Vytvoreni jednotlivych spotu v gridu
    function Spot(y, x) {
        this.y = y;
        this.x = x;
        this.isStart = this.y === START_ROW && this.x === START_COL;
        this.isEnd = this.y === END_ROW && this.x === END_COL;
        this.searched = false;
        this.isPath = false;
    };

    //Nasteveni promene isPath v kazdem spotu ktery je soucasti cesty na true
    const fillPath = (grid, path) => {
        for (let i = 0; i < path.length; i++) {
            grid[path[i][0]][path[i][1]].isPath = true;
        }
    }

    const _onMouseDown = (e) => {
        let targetDiv = e.nativeEvent;
        // console.log(targetDiv.target)
    }

    // Funkce na vykresleni gridu
    const gridwithNode = (
        <div className="Grid" onMouseDown={_onMouseDown}>
            {Grid.map((row, rowIndex) => {
                return (
                    <div key={rowIndex} className='rowWrapper'>
                        {row.map((col, colIndex) => {
                            const { isStart, isEnd, searched, isPath } = col;
                            return (
                                <Node key={colIndex} isPath={isPath} isStart={isStart} isEnd={isEnd} searched={searched} row={rowIndex} col={colIndex} />
                            )
                        })}
                    </div>
                )
            })}
        </div>
    )

    return (
        <div className='Wrapper'>
            <h1>Pathfinding</h1>
            {/* Vykresleni gridu */}
            {gridwithNode}
            <button onClick={startFinding}>Show me path</button>
        </div>
    )
}

export default Pathfind
