import React from 'react';
import './Node.css';

const Node = ({ isStart, isEnd, searched, isPath, row, col }) => {
    const classes = isStart ? "node_start" : isEnd ? "node_end" : isPath ? "node_path" : "";
    const draggAble = isStart ? true : isEnd ? true : false;

    const onDrop = (e) => {
        e.preventDefault();
        // e.dataTransfer.setData("Text", e.target.id);
        let data = e.dataTransfer.getData("text");
        console.log(data)
        // e.target.appendChild(document.getElementById(data));
    }

    const onDragStart = (e) => {
        console.log(e.target)
    }

    const onDragOver = (e) => {
        console.log(e.target.id)
        e.dataTransfer.setData("text", e.target.id)
    }

    return (
        <div id={`node-${row}-${col}`}>
            <div
                id={`${row}-${col}`}
                className={`node ${classes}`}
                draggable={draggAble}
                onDragStart={draggable ? (e) => (onDragStart(e)) : false}
                onDrop={(e) => (onDrop(e))}
                onDragOver={(e) => (onDragOver(e))}
            />
        </div>
    )
}

export default Node;
